Effects.Script:
let msg = ""

let i_gain = (await new Roll("1d10").roll()).total

if (args.actor.characteristics.i.value <= 0)
{
   i_gain += (await new Roll("2d10").roll()).total
}

msg = `<b>${this.actor.prototypeToken.name}</b> gains ${i_gain} Initiative`

let newValue = i_gain + args.actor.characteristics.i.modifier

args.actor.update({"system.characteristics.i.modifier" : newValue})


let hitloc = game.wfrp4e.tables.rollTable("hitloc")

let value = hitloc.result
let desc = hitloc.description

let effect = args.actor.effects.find(i => i.label == "Blinded Effect" && i.origin == this.effect.origin)

effect.setFlag("wfrp4e", "hitloc", value)

msg += ` as eyes push out of their ${desc}`

ChatMessage.create({content : msg, speaker : {alias : this.effect.label}})

if (args.opposedTest.result.hitloc.value == this.effect.flags.wfrp4e.hitloc) // e.g. 'head', rLeg, 'lArm'
{
     ChatMessage.create({content: `<b>${this.actor.prototypeToken.name}</b> gains a @Condition[Blinded] condition as their <b>${this.item.name}</b> was hit`})
     args.actor.addCondition("blinded")
}

if (args.opposedTest.result.hitloc.value == "head")
{
     ChatMessage.create({content : `<b>${this.actor.prototypeToken.name}</b> receives a @Table[crithead]{Critical Wound} if one was not caused already.`, speaker : {alias: this.item.name}})
}

let i_gain = (await new Roll("2d10").roll()).total
let fel_gain = 0
if (args.actor.characteristics.fel.value <= 0)
{
   fel_gain += (await new Roll("2d10").roll()).total
}

let msg = `<b>${this.actor.prototypeToken.name}</b> gains ${i_gain} Intelligence`

if (fel_gain)
   msg += ` and ${fel_gain} Fellowship`


let newInt = i_gain + args.actor.characteristics.int.modifier
let newFel = fel_gain + args.actor.characteristics.fel.modifier

args.actor.update({"system.characteristics.int.modifier" : newInt, "system.characteristics.fel.modifier" : newFel})

ChatMessage.create({content : msg, speaker : {alias : this.effect.name}})

if (args.type == "skill" &&  args.item.name.includes(game.i18n.localize("NAME.Stealth"))) {
   let sl = this.actor.characteristics.ag.bonus;
   args.prefillModifiers.slBonus += sl;
}
