Effects.Script:
if (args.type == "characteristic" && (args.item == "wp"))
{
    args.prefillModifiers.modifier += 10
}
else if (args.type == "skill" && (args.item.characteristic.key == "wp"))
{
     args.prefillModifiers.modifier += 10
}

if (args.type == "characteristic" && (args.item == "ag" || args.item == "i" || args.item == "int"))
{
    args.prefillModifiers.modifier -= 10
}
else if (args.type == "skill" && (args.item.characteristic.key == "ag" || args.item.characteristic.key == "i" || args.item.characteristic.value  == "int"))
{
     args.prefillModifiers.modifier -= 10
}
